import React, { useState, ChangeEvent, FormEvent } from 'react';

interface QueryResponse {
  response?: string;
  sources?: Array<{
    text: string;
    score: number;
    metadata: any;
    file_name?: string;
    file_type?: string;
  }>;
  file_type?: string;
  query?: string;
  detail?: string;
}

interface UploadedFile {
  name: string;
  type: string;
  size: number;
  status: string;
  result?: any;
}

interface Message {
  id: number;
  text: string;
  type: 'success' | 'error' | 'warning' | 'info';
  timestamp: number;
}

const App: React.FC = () => {
  const [query, setQuery] = useState<string>('');
  const [response, setResponse] = useState<string>('');
  const [uploadedFiles, setUploadedFiles] = useState<UploadedFile[]>([]);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [isUploading, setIsUploading] = useState<boolean>(false);
  const [isQuerying, setIsQuerying] = useState<boolean>(false);
  const [messages, setMessages] = useState<Message[]>([]);

  // Add message function
  const addMessage = (text: string, type: 'success' | 'error' | 'warning' | 'info') => {
    const newMessage: Message = {
      id: Date.now(),
      text,
      type,
      timestamp: Date.now()
    };
    setMessages(prev => [newMessage, ...prev.slice(0, 4)]); // Keep only last 5 messages

    // Auto-remove message after 5 seconds for success/info, 8 seconds for warnings/errors
    setTimeout(() => {
      setMessages(prev => prev.filter(msg => msg.id !== newMessage.id));
    }, type === 'success' || type === 'info' ? 5000 : 8000);
  };

  // Clear messages function
  const clearMessages = () => setMessages([]);

  const handleFileSelect = (e: ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      const file = files[0];
      const fileExtension = file.name.toLowerCase().split('.').pop();

      if (fileExtension && ['pdf', 'xlsx', 'xls'].includes(fileExtension)) {
        setSelectedFile(file);
      } else {
        addMessage('Please select a PDF or Excel file', 'warning');
        e.target.value = '';
      }
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      addMessage('Please select a file first', 'warning');
      return;
    }

    setIsUploading(true);

    try {
      const formData = new FormData();
      formData.append('file', selectedFile);

      const res = await fetch('/api/upload', {
        method: 'POST',
        body: formData,
      });

      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }

      const data = await res.json();

      // Refresh file list
      await fetchUploadedFiles();

      setSelectedFile(null);
      (document.getElementById('fileInput') as HTMLInputElement).value = '';

      alert(`File uploaded successfully! ${data.type} processed with ${data.result.chunks || data.result.sheets} chunks/sheets.`);

    } catch (error: unknown) {
      if (error instanceof Error) {
        alert(`Upload error: ${error.message}`);
      } else {
        alert('Upload failed');
      }
    } finally {
      setIsUploading(false);
    }
  };

  const fetchUploadedFiles = async () => {
    try {
      const res = await fetch('/api/files');
      if (res.ok) {
        const data = await res.json();
        setUploadedFiles(data.files || []);
      }
    } catch (error) {
      console.error('Error fetching files:', error);
    }
  };

  const handleQuery = async (e: FormEvent<HTMLFormElement>): Promise<void> => {
    e.preventDefault();

    if (!query.trim()) {
      alert('Please enter a query');
      return;
    }

    if (uploadedFiles.length === 0) {
      alert('Please upload at least one file first');
      return;
    }

    setIsQuerying(true);
    setResponse("Searching through your documents...");

    try {
      const res = await fetch('/api/query', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ query }),
      });

      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }

      const data: QueryResponse = await res.json();

      let responseText = data.response || 'No response';

      if (data.sources && data.sources.length > 0) {
        responseText += '\n\n--- Source Details ---\n';
        data.sources.forEach((source, index) => {
          responseText += `\nSource ${index + 1} (Score: ${source.score.toFixed(3)}):\n`;
          responseText += `File: ${source.file_name || 'Unknown'} (${source.file_type || 'Unknown'})\n`;
          responseText += `Content: ${source.text}\n`;
        });
      }

      setResponse(responseText);

    } catch (error: unknown) {
      if (error instanceof Error) {
        setResponse(`Error: ${error.message}`);
      } else {
        setResponse('Unknown error occurred.');
      }
    } finally {
      setIsQuerying(false);
    }
  };

  const deleteFile = async (filename: string) => {
    try {
      const res = await fetch(`/api/files/${filename}`, {
        method: 'DELETE',
      });

      if (res.ok) {
        await fetchUploadedFiles();
        setResponse('');
        alert(`File '${filename}' deleted successfully`);
      } else {
        throw new Error('Delete failed');
      }
    } catch (error) {
      alert(`Error deleting file: ${error}`);
    }
  };

  // Load files on component mount
  React.useEffect(() => {
    fetchUploadedFiles();
  }, []);

  const handleQueryChange = (e: ChangeEvent<HTMLTextAreaElement>) => setQuery(e.target.value);

  return (
    <div style={{ padding: '2rem', fontFamily: 'sans-serif', maxWidth: '1200px', margin: '0 auto' }}>
      <h1 style={{ color: '#333', borderBottom: '2px solid #007bff', paddingBottom: '10px' }}>
        📄 PDF & Excel RAG System
      </h1>

      {/* File Upload Section */}
      <div style={{ 
        backgroundColor: '#f8f9fa', 
        padding: '20px', 
        borderRadius: '8px', 
        marginBottom: '30px',
        border: '1px solid #dee2e6'
      }}>
        <h2 style={{ color: '#495057', marginTop: 0 }}>📤 Upload Documents</h2>
        <div style={{ marginBottom: '15px' }}>
          <input
            id="fileInput"
            type="file"
            accept=".pdf,.xlsx,.xls"
            onChange={handleFileSelect}
            style={{ marginRight: '10px' }}
          />
          <button 
            onClick={handleUpload}
            disabled={!selectedFile || isUploading}
            style={{
              backgroundColor: isUploading ? '#6c757d' : '#28a745',
              color: 'white',
              border: 'none',
              padding: '8px 16px',
              borderRadius: '4px',
              cursor: isUploading ? 'not-allowed' : 'pointer'
            }}
          >
            {isUploading ? 'Uploading...' : 'Upload File'}
          </button>
        </div>

        <p style={{ color: '#6c757d', fontSize: '0.9em', margin: 0 }}>
          Supported formats: PDF, Excel (.xlsx, .xls)
        </p>
      </div>

      {/* Uploaded Files Section */}
      {uploadedFiles.length > 0 && (
        <div style={{ 
          backgroundColor: '#e7f3ff', 
          padding: '20px', 
          borderRadius: '8px', 
          marginBottom: '30px',
          border: '1px solid #b3d9ff'
        }}>
          <h2 style={{ color: '#495057', marginTop: 0 }}>📋 Uploaded Files ({uploadedFiles.length})</h2>
          <div style={{ display: 'grid', gap: '10px' }}>
            {uploadedFiles.map((file, index) => (
              <div key={index} style={{ 
                backgroundColor: 'white',
                padding: '12px',
                borderRadius: '6px',
                border: '1px solid #ccc',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
              }}>
                <div>
                  <strong>{file.name}</strong>
                  <span style={{ 
                    marginLeft: '10px',
                    padding: '2px 8px',
                    backgroundColor: file.type === 'PDF' ? '#dc3545' : '#28a745',
                    color: 'white',
                    borderRadius: '12px',
                    fontSize: '0.8em'
                  }}>
                    {file.type}
                  </span>
                  <span style={{ marginLeft: '10px', color: '#6c757d', fontSize: '0.9em' }}>
                    {(file.size / 1024).toFixed(1)} KB
                  </span>
                </div>
                <button
                  onClick={() => deleteFile(file.name)}
                  style={{
                    backgroundColor: '#dc3545',
                    color: 'white',
                    border: 'none',
                    padding: '4px 8px',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    fontSize: '0.8em'
                  }}
                >
                  Delete
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Query Section */}
      <div style={{ 
        backgroundColor: '#fff3cd', 
        padding: '20px', 
        borderRadius: '8px', 
        marginBottom: '30px',
        border: '1px solid #ffeeba'
      }}>
        <h2 style={{ color: '#495057', marginTop: 0 }}>🔍 Query Your Documents</h2>
        <form onSubmit={handleQuery}>
          <textarea
            rows={4}
            placeholder="Ask questions about your uploaded documents..."
            value={query}
            onChange={handleQueryChange}
            style={{ 
              width: '100%', 
              marginBottom: '15px',
              padding: '10px',
              border: '1px solid #ced4da',
              borderRadius: '4px',
              fontSize: '14px',
              resize: 'vertical'
            }}
            disabled={uploadedFiles.length === 0}
          />
          <button 
            type="submit"
            disabled={uploadedFiles.length === 0 || isQuerying || !query.trim()}
            style={{
              backgroundColor: (uploadedFiles.length === 0 || isQuerying || !query.trim()) ? '#6c757d' : '#007bff',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '4px',
              cursor: (uploadedFiles.length === 0 || isQuerying || !query.trim()) ? 'not-allowed' : 'pointer',
              fontSize: '16px'
            }}
          >
            {isQuerying ? 'Searching...' : 'Search Documents'}
          </button>
        </form>

        {uploadedFiles.length === 0 && (
          <p style={{ color: '#856404', fontStyle: 'italic', marginTop: '10px' }}>
            Please upload documents first before querying.
          </p>
        )}
      </div>

      {/* Response Section */}
      {response && (
        <div style={{ 
          backgroundColor: '#d4edda', 
          padding: '20px', 
          borderRadius: '8px',
          border: '1px solid #c3e6cb'
        }}>
          <h2 style={{ color: '#495057', marginTop: 0 }}>📝 Response</h2>
          <pre style={{ 
            backgroundColor: 'white',
            padding: '15px',
            borderRadius: '4px',
            border: '1px solid #c3e6cb',
            whiteSpace: 'pre-wrap',
            fontSize: '14px',
            lineHeight: '1.5',
            maxHeight: '400px',
            overflow: 'auto'
          }}>
            {response}
          </pre>
        </div>
      )}
    </div>
  );
};

export default App;
