import React, { useState } from 'react';
import './App.css';

const API_URL = '/api/chat';

function App() {
  const [apiKey, setApiKey] = useState('');
  const [inputKey, setInputKey] = useState('');
  const [userMessage, setUserMessage] = useState('');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Ask for API key first
  if (!apiKey) {
    return (
      <div className="App">
        <h2>Enter your OpenAI API Key</h2>
        <input
          type="password"
          value={inputKey}
          onChange={e => setInputKey(e.target.value)}
          placeholder="sk-..."
          style={{ width: 300, padding: 8 }}
        />
        <br /><br />
        <button
          onClick={() => setApiKey(inputKey)}
          disabled={!inputKey}
        >
          Continue
        </button>
      </div>
    );
  }

  const handleSend = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setResponse('');
    try {
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          developer_message: 'You are a helpful AI assistant.',
          user_message: userMessage,
          model: 'gpt-4.1-mini',
          api_key: apiKey,
        })
      });
      // if (!res.ok) {
      //   const err = await res.json();
      //   throw new Error(err.detail || 'API error');
      // }
      // const data = await res.json();
      if (!response.body) {
        throw new Error('Response body is null');
      }
      const reader = response.body.getReader();
      const decoder = new TextDecoder("utf-8");

      let accumulatedResponse = ''; // Initialize a variable to hold the full response

      while (true) {
        const { done, value } = await reader.read();
        if (done) break;
        const chunk = decoder.decode(value);
        accumulatedResponse += chunk; // Append chunk to the accumulated response
        console.log(chunk); // You might want to append to UI here as well for streaming effect
      }

      setResponse(accumulatedResponse); // Set the state with the full response
    } catch (err: any) {
      setError(err.message || 'Unknown error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h2>AI Chat</h2>
      <form onSubmit={handleSend}>
        <textarea
          value={userMessage}
          onChange={e => setUserMessage(e.target.value)}
          placeholder="Type your message..."
          rows={4}
          style={{ width: 400, padding: 8 }}
        />
        <br />
        <button type="submit" disabled={loading || !userMessage}>
          {loading ? 'Sending...' : 'Send'}
        </button>
      </form>
      {error && <div style={{ color: 'red', marginTop: 16 }}>{error}</div>}
      {response && <div style={{ marginTop: 16, background: '#f5f5f5', padding: 16, borderRadius: 8 }}>{response}</div>}
      <div style={{ marginTop: 32 }}>
        <button onClick={() => setApiKey('')}>Change API Key</button>
      </div>
    </div>
  );
}

export default App;
